<template>
  <div>
    <div><img :src="currentImgUrl" alt="" class="main"></div>
    <div>
      <img
        class="child"
        v-for="(img, index) in imgList"
        :key="index"
        :src="img.url"
        alt=""
        @click="imgSelectIndex(index)"/>
      </div>
  </div>
</template>

<script>
export default {
  name: 'GalleryComp',
  data () {
    return {
      currentImgIndex: 0,
      imgList: [{ url: 'data:image/jpeg;base64,' },
        {
          url: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcThWDkX5TbS_XjMYV0cEu4kXcsTD8BT3W3FDg&usqp=CAU'
        },
        { url: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ2wC6KDczON64BPehj5F1z9guFB9XcKgllGRR9EsQ_cUB-5sAVaTZkljz1kLnEgbNxFSA&usqp=CAU' }]

    }
  },
  computed: {
    currentImgUrl () {
      return this.imgList[this.currentImgIndex].url
    }
  },
  methods: {
    imgSelectIndex (i) {
      this.currentImgIndex = i
    }
  }
}
</script>

<style>

.child{
  width: 100px;
  cursor: pointer;
  margin: 5px;
}
.main{
  width: 300px;
 }

</style>
